    private void link_Click(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_link_Click
        JTextField link = (JTextField) evt.getSource();
        openSite(link.getText());
    }//GEN-LAST:event_link_Click

    private void openSite(String site) {
        try {
            URI url = new URI(site);
            Desktop.getDesktop().browse(url);
        }
        catch(URISyntaxException | IOException ex) {}
    }
